'use strict'

const headers = {
  plain: {'content-type': 'text/plain'}
}

module.exports['404'] = function NotFound_404 (request, reply) {
  reply(`Resource ${request.url} not found`, 404, headers.plain)
}

module.exports['405'] = function MethodNotAllowed_405 (request, reply) {
  reply(`Method ${request.method} not allowed on ${request.url}`, 405, headers.plain)
}
